// ChainSync Service Worker for Offline Capabilities
// Phase 8 - Future Enhancements

// Enhanced development mode detection
const isDevelopment = () => {
  // Check multiple development indicators
  const hostname = self.location.hostname;
  const port = self.location.port;

  return (
    hostname === 'localhost' || 
    hostname === '127.0.0.1' || 
    hostname.includes('replit') ||
    hostname.includes('dev') ||
    port === '3000' ||
    port === '5173' ||
    port === '8080'
  );
};

// Check if we're in development mode (allow override for E2E)
if (isDevelopment() && !(self && self.__E2E_ENABLE_SW === true)) {
  console.log('Service Worker disabled in development mode');
  // Don't register any other event listeners in development
  // Use a function to avoid illegal return statement
  const disableInDevelopment = () => {
    // Early exit for development
  };
  disableInDevelopment();
}

async function precacheBuildAssets(cache) {
  try {
    const response = await fetch(ASSET_MANIFEST_URL, { cache: 'no-store' });
    if (!response.ok) {
      console.warn('Asset manifest fetch failed', response.status);
      return;
    }

    const manifest = await response.json().catch(() => null);
    if (!manifest) {
      console.warn('Asset manifest response was empty');
      return;
    }

    const assetSet = new Set();

    if (Array.isArray(manifest.files)) {
      manifest.files.forEach((file) => {
        if (typeof file === 'string' && file.trim().length > 0) {
          assetSet.add(file.trim());
        }
      });
    }

    if (Array.isArray(manifest)) {
      manifest.forEach((file) => {
        if (typeof file === 'string' && file.trim().length > 0) {
          assetSet.add(file.trim());
        }
      });
    }

    if (manifest && typeof manifest === 'object' && !Array.isArray(manifest)) {
      Object.values(manifest).forEach((entry) => {
        if (!entry || typeof entry !== 'object') return;
        if (typeof entry.file === 'string') {
          assetSet.add(entry.file);
        }
        if (Array.isArray(entry.css)) {
          entry.css.forEach((cssFile) => {
            if (typeof cssFile === 'string') assetSet.add(cssFile);
          });
        }
        if (Array.isArray(entry.assets)) {
          entry.assets.forEach((assetFile) => {
            if (typeof assetFile === 'string') assetSet.add(assetFile);
          });
        }
      });
    }

    if (assetSet.size === 0) {
      console.warn('Asset manifest contained no precacheable files');
      return;
    }

    const results = await Promise.allSettled(
      Array.from(assetSet).map(async (filePath) => {
        const normalized = filePath.startsWith('/') ? filePath : `/${filePath}`;
        try {
          await cache.add(normalized);
        } catch (err) {
          console.warn('Skipping build asset from precache', normalized, err);
        }
      })
    );

    const skipped = results.filter((r) => r.status === 'rejected');
    if (skipped.length > 0) {
      console.warn('Some build assets failed to precache');
    }
  } catch (error) {
    console.warn('Failed to precache build assets', error);
  }
}

const CACHE_NAME = 'chainsync-v1.0.2';
const OFFLINE_CACHE = 'chainsync-offline-v1.0.2';
let isDisabled = false;
let heartbeatTimer = null;
const ASSET_MANIFEST_URL = '/asset-manifest.json';

// Essential resources to cache for offline use
const ESSENTIAL_RESOURCES = [
  '/',
  '/index.html',
  '/manifest.json',
  '/icons/icon-192.png',
  '/icons/icon-512.png'
];

// API endpoints that should work offline
const OFFLINE_ENDPOINTS = [
  '/api/pos/sales',
  '/api/inventory',
  '/api/products',
  '/api/stores'
];

// Install event - cache essential resources
self.addEventListener('install', (event) => {
  event.waitUntil(
    caches.open(CACHE_NAME)
      .then(async (cache) => {
        console.log('Caching essential resources for offline use');
        const results = await Promise.allSettled(
          ESSENTIAL_RESOURCES.map(async (resource) => {
            try {
              await cache.add(resource);
            } catch (err) {
              console.warn('Skipping missing resource from precache', resource, err);
            }
          })
        );
        const rejected = results.filter((result) => result.status === 'rejected');
        if (rejected.length > 0) {
          console.warn('Some resources failed precache and were skipped');
        }

        await precacheBuildAssets(cache);
      })
      .then(() => {
        console.log('Service Worker installed successfully');
        return self.skipWaiting();
      })
      .catch((error) => {
        console.error('Service Worker installation failed:', error);
      })
  );
});

// Activate event - clean up old caches
self.addEventListener('activate', (event) => {
  event.waitUntil(
    caches.keys()
      .then((cacheNames) => {
        return Promise.all(
          cacheNames.map((cacheName) => {
            if (cacheName !== CACHE_NAME && cacheName !== OFFLINE_CACHE) {
              console.log('Deleting old cache:', cacheName);
              return caches.delete(cacheName);
            }
          })
        );
      })
      .then(() => {
        console.log('Service Worker activated');
        return self.clients.claim();
      })
  );
  // Lightweight heartbeat to keep SW alive and schedule periodic sync checks
  try {
    if (heartbeatTimer) clearInterval(heartbeatTimer);
    heartbeatTimer = setInterval(() => {
      // noop; could postMessage to clients if needed
    }, 60 * 60 * 1000); // hourly noop
  } catch {}
});

// Fetch event - handle offline requests
self.addEventListener('fetch', (event) => {
  // Skip if service worker is disabled
  if (isDisabled) {
    return;
  }

  const { request } = event;
  const url = new URL(request.url);

  // For POST to POS sales, let network happen; on failure, client enqueues
  if (request.method !== 'GET') {
    return;
  }

  // Handle API requests
  if (url.pathname.startsWith('/api/')) {
    event.respondWith(handleApiRequest(request));
    return;
  }

  // Handle static resources
  if (url.pathname.startsWith('/src/') || url.pathname.startsWith('/assets/')) {
    event.respondWith(handleStaticResource(request));
    return;
  }

  // Handle navigation requests
  if (request.mode === 'navigate') {
    event.respondWith(handleNavigation(request));
    return;
  }
});

// Handle API requests with offline support
async function handleApiRequest(request) {
  try {
    // Try network first
    const response = await fetch(request);

    // Cache successful responses for offline use
    if (response.ok && isCacheableEndpoint(request.url)) {
      const cache = await caches.open(OFFLINE_CACHE);
      cache.put(request, response.clone());
    }

    return response;
  } catch (error) {
    console.log('Network failed, trying cache:', request.url);

    // Try cache for offline endpoints
    if (isOfflineEndpoint(request.url)) {
      const cachedResponse = await caches.match(request);
      if (cachedResponse) {
        return cachedResponse;
      }
    }

    // For critical POS sales endpoint, do not fabricate success.
    // Let page logic enqueue offline; return 503 to trigger fallback UI
    if (isCriticalEndpoint(request.url)) {
      return new Response(JSON.stringify({ status: 'offline', message: 'Saved for offline sync (client)', data: null }), { status: 503, headers: { 'Content-Type': 'application/json' } });
    }

    // Don't throw error, just return a fallback response
    console.warn('API request failed, returning fallback:', request.url);
    return new Response(JSON.stringify({
      status: 'error',
      message: 'Service temporarily unavailable',
      data: null
    }), {
      status: 503,
      headers: { 'Content-Type': 'application/json' }
    });
  }
}

// Handle static resources
async function handleStaticResource(request) {
  try {
    // Try cache first for static resources
    const cachedResponse = await caches.match(request);
    if (cachedResponse) {
      return cachedResponse;
    }

    // Fallback to network
    const response = await fetch(request);
    if (response.ok) {
      const cache = await caches.open(CACHE_NAME);
      cache.put(request, response.clone());
    }

    return response;
  } catch (error) {
    console.error('Static resource fetch failed:', error);
    // Return a fallback response instead of throwing
    return new Response('Resource not available', {
      status: 404,
      headers: { 'Content-Type': 'text/plain' }
    });
  }
}

// Handle navigation requests
async function handleNavigation(request) {
  try {
    // Special handling for payment callback pages
    const url = new URL(request.url);
    if (url.pathname === '/payment/callback') {
      console.log('Payment callback navigation detected, serving directly from network');

      try {
        const response = await fetch(request);
        return response;
      } catch (error) {
        console.log('Payment callback network failed, serving offline page');
        // Return cached index.html for offline payment callback
        const cachedResponse = await caches.match('/index.html');
        if (cachedResponse) {
          return cachedResponse;
        }
        throw error;
      }
    }

    // Try network first for other navigation requests
    const response = await fetch(request);
    return response;
  } catch (error) {
    console.log('Navigation failed, serving offline page');

    // Return cached index.html for offline navigation
    const cachedResponse = await caches.match('/index.html');
    if (cachedResponse) {
      return cachedResponse;
    }

    // Return a fallback response instead of throwing
    return new Response('Page not available offline', {
      status: 503,
      headers: { 'Content-Type': 'text/html' }
    });
  }
}

// Check if endpoint should be cached
function isCacheableEndpoint(url) {
  const cacheablePatterns = [
    '/api/products',
    '/api/stores',
    '/api/inventory'
  ];

  return cacheablePatterns.some(pattern => url.includes(pattern));
}

// Check if endpoint should work offline
function isOfflineEndpoint(url) {
  return OFFLINE_ENDPOINTS.some(endpoint => url.includes(endpoint));
}

// Check if endpoint is critical for offline operation
function isCriticalEndpoint(url) {
  const criticalPatterns = [
    '/api/pos/sales',
    '/api/products/barcode'
  ];

  return criticalPatterns.some(pattern => url.includes(pattern));
}

// Create offline response for critical endpoints
function createOfflineResponse(request) {
  const url = new URL(request.url);
  
  // Return appropriate offline response based on endpoint
  if (url.pathname.startsWith('/api/pos/sales')) {
    return new Response(JSON.stringify({
      status: 'offline',
      message: 'Sale saved locally. Will sync when online.',
      data: { localId: generateLocalId() }
    }), {
      status: 200,
      headers: { 'Content-Type': 'application/json' }
    });
  }
  
  if (url.pathname.startsWith('/api/products/barcode')) {
    return new Response(JSON.stringify({
      status: 'offline',
      message: 'Product lookup unavailable offline',
      data: null
    }), {
      status: 503,
      headers: { 'Content-Type': 'application/json' }
    });
  }
  
  // Default offline response
  return new Response(JSON.stringify({
    status: 'offline',
    message: 'Service temporarily unavailable',
    data: null
  }), {
    status: 503,
    headers: { 'Content-Type': 'application/json' }
  });
}

// Generate local ID for offline transactions
function generateLocalId() {
  return `local_${Date.now()}_${Math.random().toString(36).substring(2, 15)}`;
}

// Background sync for offline data
self.addEventListener('sync', (event) => {
  if (event.tag === 'background-sync') {
    event.waitUntil(syncOfflineData());
  }
});

// Also allow manual trigger from client
self.addEventListener('message', (event) => {
  try {
    if (event.data && event.data.type === 'TRY_SYNC') {
      event.waitUntil(syncOfflineData());
    }
  } catch {}
});

// Sync offline data when connection is restored
// IndexedDB helpers
const DB_NAME = 'chainsync_offline';
const STORE = 'offline_sales';

function openDb() {
  return new Promise((resolve) => {
    if (typeof indexedDB === 'undefined') return resolve(null);
    const request = indexedDB.open(DB_NAME, 1);
    request.onupgradeneeded = () => {
      const db = request.result;
      if (!db.objectStoreNames.contains(STORE)) {
        const store = db.createObjectStore(STORE, { keyPath: 'id' });
        store.createIndex('nextAttemptAt', 'nextAttemptAt', { unique: false });
        store.createIndex('createdAt', 'createdAt', { unique: false });
      }
    };
    request.onsuccess = () => resolve(request.result);
    request.onerror = () => resolve(null);
  });
}

async function getAllOfflineSales() {
  const db = await openDb();
  if (!db) return [];
  return await new Promise((resolve) => {
    const tx = db.transaction(STORE, 'readonly');
    const store = tx.objectStore(STORE);
    const req = store.getAll();
    req.onsuccess = () => resolve(req.result || []);
    req.onerror = () => resolve([]);
  });
}

async function deleteOfflineSale(id) {
  const db = await openDb();
  if (!db) return;
  await new Promise((resolve) => {
    const tx = db.transaction(STORE, 'readwrite');
    tx.objectStore(STORE).delete(id);
    tx.oncomplete = () => resolve();
    tx.onerror = () => resolve();
  });
}

async function updateOfflineSaleFailure(id, lastError) {
  const db = await openDb();
  if (!db) return;
  await new Promise((resolve) => {
    const tx = db.transaction(STORE, 'readwrite');
    const store = tx.objectStore(STORE);
    const getReq = store.get(id);
    getReq.onsuccess = () => {
      const rec = getReq.result;
      if (!rec) return resolve();
      const attempts = (rec.attempts || 0) + 1;
      // capped exponential backoff (5m) + jitter
      const base = Math.pow(2, attempts) * 1000;
      const jitter = Math.floor(Math.random() * 500);
      const delayMs = Math.min(300000, base + jitter);
      rec.attempts = attempts;
      rec.lastError = String(lastError || 'unknown error');
      rec.nextAttemptAt = Date.now() + delayMs;
      store.put(rec);
    };
    tx.oncomplete = () => resolve();
    tx.onerror = () => resolve();
  });
}

async function syncOfflineData() {
  try {
    console.log('Starting background sync...');
    const offlineData = await getAllOfflineSales();
    
    if (offlineData.length === 0) {
      console.log('No offline data to sync');
      return;
    }
    
    const now = Date.now();
    let syncedCount = 0;
    for (const item of offlineData) {
      try {
        if (item.nextAttemptAt && item.nextAttemptAt > now) continue;
        const res = await syncOfflineItem(item);
        if (res.ok || res.status === 409) {
          // Try to parse response for details (receipt, sale id)
          let payload = null;
          try {
            const ct = res.headers.get('content-type') || '';
            if (ct.includes('application/json')) payload = await res.clone().json();
          } catch {}
          await deleteOfflineSale(item.id);
          syncedCount++;
          // Notify clients of each synced sale
          try {
            const clientsList = await self.clients.matchAll();
            clientsList.forEach((client) => {
              client.postMessage({
                type: 'SYNC_SALE_OK',
                data: {
                  idempotencyKey: item.idempotencyKey,
                  status: res.status,
                  sale: payload || null,
                }
              });
            });
          } catch {}
        } else {
          await updateOfflineSaleFailure(item.id, `HTTP ${res.status}`);
        }
      } catch (error) {
        console.error('Failed to sync offline item:', error);
        await updateOfflineSaleFailure(item.id, error?.message || 'error');
      }
    }
    
    console.log('Background sync completed');
    
    // Notify clients of sync completion
    const clients = await self.clients.matchAll();
    clients.forEach(client => {
      client.postMessage({
        type: 'SYNC_COMPLETED',
        data: { attempted: offlineData.length, synced: syncedCount }
      });
    });
  } catch (error) {
    console.error('Background sync failed:', error);
  }
}

// Get offline data from IndexedDB
async function syncOfflineItem(item) {
  const response = await fetch(item.url, {
    method: 'POST',
    headers: item.headers || { 'Content-Type': 'application/json', 'Idempotency-Key': item.idempotencyKey },
    body: JSON.stringify(item.payload),
    credentials: 'include',
  });
  return response;
}

// Handle push notifications
self.addEventListener('push', (event) => {
  if (event.data) {
    const data = event.data.json();
    // ...
    const options = {
      body: data.message,
      icon: '/icon-192x192.png',
      badge: '/badge-72x72.png',
      tag: data.tag || 'chainsync-notification',
      data: data.data || {},
      actions: data.actions || [],
      requireInteraction: data.priority === 'high'
    };
    
    event.waitUntil(
      self.registration.showNotification(data.title, options)
    );
  }
});

// Handle notification clicks
self.addEventListener('notificationclick', (event) => {
  event.notification.close();
  
  if (event.action) {
    // Handle specific notification actions
    handleNotificationAction(event.action, event.notification.data);
  } else {
    // Default action - focus or open the app
    event.waitUntil(
      self.clients.matchAll({ type: 'window' })
        .then((clients) => {
          if (clients.length > 0) {
            return clients[0].focus();
          } else {
            return self.clients.openWindow('/');
          }
        })
    );
  }
});

// Handle notification actions
function handleNotificationAction(action, data) {
  switch (action) {
    case 'view-alert':
      self.clients.openWindow(`/alerts?id=${data.alertId}`);
      break;
    case 'view-inventory':
      self.clients.openWindow(`/inventory?product=${data.productId}`);
      break;
    default:
      console.log('Unknown notification action:', action);
  }
}

// Handle message events from main thread
self.addEventListener('message', (event) => {
  const { type, data } = event.data;
  
  switch (type) {
    case 'SKIP_WAITING':
      self.skipWaiting();
      break;
    case 'GET_VERSION':
      event.ports[0].postMessage({ version: CACHE_NAME });
      break;
    case 'CLEAR_CACHE':
      clearCache().then(() => {
        event.ports[0].postMessage({ success: true });
      });
      break;
    case 'PREWARM_CACHES':
      // Only prewarm POS data when explicitly allowed by the client
      // Accept either an authenticated signal or an explicit feature flag
      event.waitUntil((async () => {
        try {
          const allowPrewarm = !!(data && (data.authenticated === true || data.allowPrewarm === true));
          if (!allowPrewarm) return; // silent no-op when unauthenticated
          const cache = await caches.open(OFFLINE_CACHE);
          const urls = ['/api/products', '/api/stores', '/api/inventory'];
          await Promise.all(urls.map(async (u) => {
            try {
              const res = await fetch(u, { credentials: 'include' });
              if (res.ok) await cache.put(u, res.clone());
            } catch {}
          }));
        } catch {}
      })());
      break;
    case 'DISABLE':
      isDisabled = true;
      console.log('Service Worker disabled');
      // Clear all caches when disabled
      clearCache();
      break;
    default:
      console.log('Unknown message type:', type);
  }
});

// Clear all caches
async function clearCache() {
  const cacheNames = await caches.keys();
  await Promise.all(
    cacheNames.map(cacheName => caches.delete(cacheName))
  );
  console.log('All caches cleared');
}

// Periodic cache cleanup
setInterval(async () => {
  try {
    const cache = await caches.open(OFFLINE_CACHE);
    const requests = await cache.keys();
    
    // Remove old cached responses (older than 7 days)
    const cutoff = Date.now() - (7 * 24 * 60 * 60 * 1000);
    
    for (const request of requests) {
      const response = await cache.match(request);
      const date = response.headers.get('date');
      
      if (date && new Date(date).getTime() < cutoff) {
        await cache.delete(request);
      }
    }
    // Limit cache size for offline cache to avoid unbounded growth
    const refreshedKeys = await cache.keys();
    const MAX_ENTRIES = 200;
    if (refreshedKeys.length > MAX_ENTRIES) {
      const toDelete = refreshedKeys.slice(0, refreshedKeys.length - MAX_ENTRIES);
      for (const req of toDelete) {
        await cache.delete(req);
      }
    }
  } catch (error) {
    console.error('Cache cleanup failed:', error);
  }
}, 24 * 60 * 60 * 1000); // Run daily

import { queryClient } from "./lib/queryClient";
import { QueryClientProvider } from "@tanstack/react-query";
import React, { Suspense, lazy, useEffect, useState } from 'react';
import { Switch, Route, useLocation } from "wouter";
import { ErrorBoundary } from "@/components/error-boundary";
import { PageLoading } from "@/components/ui/loading";
import { Toaster } from "@/components/ui/toaster";
import { TooltipProvider } from "@/components/ui/tooltip";
import { AIChatProvider } from "@/hooks/use-ai-chat";
import { useAuth } from "@/hooks/use-auth";
import { ScannerProvider } from "@/hooks/use-barcode-scanner";
import { useProtectedRouteGuard } from "@/hooks/use-protected-route-guard";
const Login = lazy(() => import("@/components/auth/login"));
const Signup = lazy(() => import("@/components/auth/signup"));
const ForgotPassword = lazy(() => import("@/components/auth/forgot-password"));
const ResetPassword = lazy(() => import("@/components/auth/reset-password"));
const ForcePasswordReset = lazy(() => import("./components/auth/force-password-reset"));
const MainLayout = lazy(() => import("@/components/layout/main-layout"));
import { RECAPTCHA_SITE_KEY } from './lib/constants';

// Lazy load pages for better performance
const Landing = lazy(() => import("@/pages/landing"));
const PaymentCallback = lazy(() => import("@/pages/payment-callback"));
const POS = lazy(() => import("@/pages/pos"));
const Inventory = lazy(() => import("@/pages/inventory"));
const Analytics = lazy(() => import("@/pages/analytics"));  
const AdminAudit = lazy(() => import("@/pages/admin/audit"));
const AdminUsers = lazy(() => import("@/pages/admin/users"));
const AdminIp = lazy(() => import("@/pages/admin/ip-whitelist"));
const AdminBulk = lazy(() => import("@/pages/admin/bulk-pricing"));
const AdminBilling = lazy(() => import("@/pages/admin/billing"));
const PostOnboarding = lazy(() => import("@/pages/post-onboarding"));
const Loyalty = lazy(() => import("@/pages/loyalty"));
const Alerts = lazy(() => import("@/pages/alerts"));
const DataImport = lazy(() => import("@/pages/data-import"));
const MultiStore = lazy(() => import("@/pages/multi-store"));
const Settings = lazy(() => import("@/pages/settings"));
const StoreStaff = lazy(() => import("./pages/store-staff"));
const DebugCsrf = lazy(() => import("@/pages/debug-csrf"));
const NotFound = lazy(() => import("@/pages/not-found"));


// Loading component for lazy-loaded pages
const PageLoader = () => (
  <div className="flex items-center justify-center min-h-screen">
    <PageLoading />
  </div>
);

// Dashboard component for different user roles
function Dashboard({ userRole }: { userRole: string }) {
  // Ensure userRole is always a valid string; default to "admin" for new signups
  const role = userRole || "admin";
  console.log("Dashboard rendering with role:", role);
  const [location, setLocation] = useLocation();

  useEffect(() => {
    const defaultPath = role === "admin" ? "/analytics" : role === "manager" ? "/inventory" : "/pos";
    if (location === "/login" || location === "/") {
      setLocation(defaultPath, { replace: true });
    }
  }, [location, setLocation, role]);

  if (role === "admin") {
    return (
      <ScannerProvider>
        <MainLayout userRole={role}>
          <Suspense fallback={<PageLoader />}>
            <Switch>
              <Route path="/" component={Analytics} /> {/* Admin sees analytics as default */}
              <Route path="/login" component={Analytics} /> {/* Redirect login to default */}
              <Route path="/inventory" component={Inventory} />
              <Route path="/analytics" component={Analytics} />
              <Route path="/admin/audit" component={AdminAudit} />
              <Route path="/admin/users" component={AdminUsers} />
              <Route path="/admin/ip-whitelist" component={AdminIp} />
              <Route path="/admin/bulk-pricing" component={AdminBulk} />
              <Route path="/admin/billing" component={AdminBilling} />
              <Route path="/loyalty" component={Loyalty} />
              <Route path="/alerts" component={Alerts} />
              <Route path="/data-import" component={DataImport} />
              <Route path="/multi-store" component={MultiStore} />
              <Route path="/settings" component={Settings} />
              <Route path="/stores/:storeId/staff" component={StoreStaff} />
              <Route path="/pos" component={POS} />
              <Route path="/debug-csrf" component={DebugCsrf} />
              <Route component={NotFound} />
            </Switch>
          </Suspense>
        </MainLayout>
      </ScannerProvider>
    );
  }

  if (role === "manager") {
    return (
      <ScannerProvider>
        <MainLayout userRole={role}>
          <Suspense fallback={<PageLoader />}>
            <Switch>
              <Route path="/" component={Inventory} /> {/* Manager sees inventory as default */}
              <Route path="/login" component={Inventory} /> {/* Redirect login to default */}
              <Route path="/inventory" component={Inventory} />
              <Route path="/analytics" component={Analytics} />
              <Route path="/admin/audit" component={AdminAudit} />
              <Route path="/admin/users" component={AdminUsers} />
              <Route path="/admin/ip-whitelist" component={AdminIp} />
              <Route path="/admin/bulk-pricing" component={AdminBulk} />
              <Route path="/loyalty" component={Loyalty} />
              <Route path="/alerts" component={Alerts} />
              <Route path="/data-import" component={DataImport} />
              <Route path="/settings" component={Settings} />
              <Route path="/pos" component={POS} />
              <Route component={NotFound} />
            </Switch>
          </Suspense>
        </MainLayout>
      </ScannerProvider>
    );
  }

  // Cashier role (default) - redirect directly to POS
  return (
    <ScannerProvider>
      <MainLayout userRole={role}>
        <Suspense fallback={<PageLoader />}>
          <Switch>
            <Route path="/" component={POS} /> {/* Cashier sees POS as default */}
            <Route path="/login" component={POS} /> {/* Redirect login to default */}
            <Route path="/pos" component={POS} />
            <Route component={NotFound} />
          </Switch>
        </Suspense>
      </MainLayout>
    </ScannerProvider>
  );
}

function App() {
  const { user, login, isAuthenticated, isLoading, error, requiresPasswordChange } = useAuth();
  const [showForgotPassword, setShowForgotPassword] = useState(false);
  const [location, setLocation] = useLocation();

  useEffect(() => {
    if (isAuthenticated && requiresPasswordChange && location !== "/force-password-reset") {
      setLocation("/force-password-reset");
    }
  }, [isAuthenticated, requiresPasswordChange, location, setLocation]);

  useProtectedRouteGuard(isLoading, isAuthenticated);

  // Debug: Log reCAPTCHA site key
  console.log('App loaded, RECAPTCHA_SITE_KEY:', RECAPTCHA_SITE_KEY);
  console.log('Environment variables:', {
    VITE_RECAPTCHA_SITE_KEY: import.meta.env.VITE_RECAPTCHA_SITE_KEY,
    NODE_ENV: import.meta.env.NODE_ENV,
    MODE: import.meta.env.MODE
  });

  return (
    <ErrorBoundary>
      <QueryClientProvider client={queryClient}>
        <AIChatProvider>
          <TooltipProvider>
              <Toaster />
              {isLoading ? (
                <PageLoader />
              ) : isAuthenticated && user ? (
                requiresPasswordChange ? (
                  <Suspense fallback={<PageLoader />}>
                    <ForcePasswordReset />
                  </Suspense>
                ) : (
                  <Dashboard userRole={user.role} />
                )
              ) : (
                <Suspense fallback={<PageLoader />}>
                  <Switch>
                    <Route path="/login">{() => (
                      <Login
                        onLogin={login}
                        onForgotPassword={() => setLocation('/forgot-password')}
                        isLoading={isLoading}
                        error={error || null}
                      />
                    )}</Route>
                    <Route path="/signup" component={Signup} />
                    <Route path="/forgot-password">{() => (
                      <ForgotPassword onBackToLogin={() => setLocation('/login')} />
                    )}</Route>
                    <Route path="/reset-password">{() => {
                      const token = typeof window !== 'undefined' ? (new URLSearchParams(window.location.search).get('token') || '') : '';
                      return (
                        <ResetPassword
                          token={token}
                          onSuccess={() => setLocation('/login')}
                        />
                      );
                    }}</Route>
                    <Route path="/" component={Landing} />
                    <Route component={NotFound} />
                  </Switch>
                </Suspense>
              )}
            </TooltipProvider>
        </AIChatProvider>
      </QueryClientProvider>
    </ErrorBoundary>
  );
}

