  { value: "critical", label: "Critical", color: "text-red-600 bg-red-50 border-red-200" },
  { value: "high", label: "High", color: "text-orange-600 bg-orange-50 border-orange-200" },
  { value: "medium", label: "Medium", color: "text-yellow-600 bg-yellow-50 border-yellow-200" },
  { value: "low", label: "Low", color: "text-green-600 bg-green-50 border-green-200" },
];

export default function AiInsights({ storeId, className }: AiInsightsProps) {
  const [selectedType, setSelectedType] = useState("all");
  const [selectedSeverity, setSelectedSeverity] = useState("all");
  const [activeTab, setActiveTab] = useState("overview");

  const queryClient = useQueryClient();

  // Fetch AI insights from backend and normalize shape
  const { data: insights = [], isLoading } = useQuery<AiInsight[]>({
    queryKey: ["/api/ai/insights", storeId],
    queryFn: async () => {
      const resp: any = await apiClient.get<any>(`/ai/insights`, { storeId });
      // Server returns { enabled, insights, summary, metadata }
      const rawInsights = Array.isArray(resp?.insights) ? resp.insights : [];
      return rawInsights.map((ins: any, idx: number): AiInsight => ({
        id: ins.id || `${idx}-${ins.generatedAt || Date.now()}`,
        insightType: ins.category || "general",
        title: ins.title || "Insight",
        description: ins.description || "",
        severity: ins.priority || "medium",
        data: {
          impact: typeof ins.impact === 'number' ? ins.impact : undefined,
          confidence: typeof ins.confidence === 'number' ? Math.round((ins.confidence || 0) * 100) : undefined,
          recommendations: ins.recommendedActions || [],
          metrics: ins.metrics || {},
        },
        isRead: false,
        isActioned: false,
        createdAt: ins.generatedAt || new Date().toISOString(),
      }));
    },
  });

  // Calculate summary statistics
  const summary: InsightSummary = {
    total: insights.length,
    unread: insights.filter(i => !i.isRead).length,
    critical: insights.filter(i => i.severity === "critical").length,
    high: insights.filter(i => i.severity === "high").length,
    medium: insights.filter(i => i.severity === "medium").length,
    low: insights.filter(i => i.severity === "low").length,
    byType: insights.reduce((acc, insight) => {
      acc[insight.insightType] = (acc[insight.insightType] || 0) + 1;
      return acc;
    }, {} as Record<string, number>),
  };

  // Filter insights based on selected criteria
  const filteredInsights = insights.filter(insight => {
    const typeMatch = selectedType === "all" || insight.insightType === selectedType;
    const severityMatch = selectedSeverity === "all" || insight.severity === selectedSeverity;
    return typeMatch && severityMatch;
  });

  const getSeverityColor = (severity: string) => {
    const level = SEVERITY_LEVELS.find(l => l.value === severity);
    return level?.color || "text-gray-600 bg-gray-50 border-gray-200";
  };

  const getSeverityIcon = (severity: string) => {
    switch (severity) {
      case "critical": return AlertTriangle;
      case "high": return AlertTriangle;
      case "medium": return Clock;
      case "low": return CheckCircle;
      default: return Clock;
    }
  };

  const getInsightIcon = (type: string) => {
    const insightType = INSIGHT_TYPES.find(t => t.value === type);
    return insightType?.icon || Brain;
  };

  const markAsRead = async (insightId: string) => {
    // Optimistic local update as backend route doesn't exist yet
    queryClient.setQueryData<AiInsight[]>(["/api/ai/insights", storeId], (prev) => {
      if (!prev) return prev as any;
      return prev.map(i => i.id === insightId ? { ...i, isRead: true } : i);
    });
  };

  const markAsActioned = async (insightId: string) => {
    // Optimistic local update as backend route doesn't exist yet
    queryClient.setQueryData<AiInsight[]>(["/api/ai/insights", storeId], (prev) => {
      if (!prev) return prev as any;
      return prev.map(i => i.id === insightId ? { ...i, isActioned: true } : i);
    });
  };

  const renderInsightCard = (insight: AiInsight) => {
    const Icon = getSeverityIcon(insight.severity);
    const TypeIcon = getInsightIcon(insight.insightType);

    return (
      <div
        key={insight.id}
        className={cn(
          "p-4 rounded-lg border transition-all",
          getSeverityColor(insight.severity),
          !insight.isRead && "ring-2 ring-blue-200",
          insight.isActioned && "opacity-75"
        )}
      >
        <div className="flex items-start space-x-3">
          <Icon className="w-5 h-5 mt-0.5 flex-shrink-0" />
          <div className="flex-1">
            <div className="flex items-center space-x-2 mb-1">
              <h4 className="font-medium">{insight.title}</h4>
              <Badge variant="outline" className="text-xs">
                <TypeIcon className="w-3 h-3 mr-1" />
                {insight.insightType}
              </Badge>
              <Badge 
                variant={insight.severity === "critical" ? "destructive" : "secondary"}
                className="text-xs"
              >
                {insight.severity}
              </Badge>
              {!insight.isRead && (
                <Badge variant="default" className="text-xs">
                  New
                </Badge>
              )}
              {insight.isActioned && (
                <Badge variant="outline" className="text-xs">
                  Actioned
                </Badge>
              )}
            </div>
            <p className="text-sm mb-3">{insight.description}</p>
            
            {/* Insight Data Visualization */}
            {insight.data && (
              <div className="mb-3 p-3 bg-white/50 rounded border">
                <h5 className="text-sm font-medium mb-2">Data Analysis</h5>
                {insight.data.impact && (
                  <div className="flex items-center space-x-2 mb-2">
                    <span className="text-xs text-gray-600">Impact:</span>
                    <Badge 
                      variant={insight.data.impact > 0 ? "default" : "secondary"}
                      className="text-xs"
                    >
                      {insight.data.impact > 0 ? "+" : ""}{insight.data.impact}%
                    </Badge>
                  </div>
                )}
                {insight.data.confidence && (
                  <div className="mb-2">
                    <div className="flex justify-between text-xs text-gray-600 mb-1">
                      <span>Confidence</span>
                      <span>{insight.data.confidence}%</span>
                    </div>
                    <Progress value={insight.data.confidence} className="h-2" />
                  </div>
                )}
                {insight.data.recommendations && (
                  <div className="text-xs">
                    <span className="text-gray-600">Recommendations:</span>
                    <ul className="list-disc list-inside mt-1 space-y-1">
                      {insight.data.recommendations.map((rec: string, idx: number) => (
                        <li key={idx}>{rec}</li>
                      ))}
                    </ul>
                  </div>
                )}
              </div>
            )}

            <div className="flex items-center justify-between">
              <span className="text-xs text-gray-500">
                {formatDate(new Date(insight.createdAt), "MMM dd, yyyy HH:mm")}
              </span>
              <div className="flex items-center space-x-2">
                {!insight.isRead && (
                  <Button 
                    variant="ghost" 
                    size="sm"
                    onClick={() => markAsRead(insight.id)}
                  >
                    Mark Read
                  </Button>
                )}
                {!insight.isActioned && (
                  <Button 
                    variant="outline" 
                    size="sm"
                    onClick={() => markAsActioned(insight.id)}
                  >
                    Mark Actioned
                  </Button>
                )}
                <Button variant="ghost" size="sm">
                  View Details
                </Button>
              </div>
            </div>
          </div>
        </div>
      </div>
    );
  };

  return (
    <div className={cn("space-y-6", className)}>
      {/* Critical Alerts */}
      {summary.critical > 0 && (
        <Alert className="border-red-200 bg-red-50">
          <AlertTriangle className="h-4 w-4 text-red-600" />
          <AlertDescription className="text-red-800">
            <strong>{summary.critical} critical insights</strong> require immediate attention. 
            Review and take action on these high-priority recommendations.
          </AlertDescription>
        </Alert>
      )}

      <Tabs value={activeTab} onValueChange={setActiveTab}>
        <TabsList className="grid w-full grid-cols-3">
          <TabsTrigger value="overview">Overview</TabsTrigger>
          <TabsTrigger value="insights">All Insights</TabsTrigger>
          <TabsTrigger value="actions">Action Items</TabsTrigger>
        </TabsList>

        <TabsContent value="overview" className="space-y-6">
          {/* Summary Cards */}
          <div className="grid grid-cols-1 md:grid-cols-4 gap-4">
            <Card>
              <CardHeader className="flex flex-row items-center justify-between space-y-0 pb-2">
                <CardTitle className="text-sm font-medium">Total Insights</CardTitle>
                <Brain className="h-4 w-4 text-muted-foreground" />
              </CardHeader>
              <CardContent>
                <div className="text-2xl font-bold">{summary.total}</div>
                <p className="text-xs text-muted-foreground">
                  {summary.unread} unread
                </p>
              </CardContent>
            </Card>

            <Card>
              <CardHeader className="flex flex-row items-center justify-between space-y-0 pb-2">
                <CardTitle className="text-sm font-medium">Critical</CardTitle>
                <AlertTriangle className="h-4 w-4 text-red-600" />
              </CardHeader>
              <CardContent>
                <div className="text-2xl font-bold text-red-600">{summary.critical}</div>
                <p className="text-xs text-muted-foreground">
                  Requires immediate action
                </p>
              </CardContent>
            </Card>

            <Card>
              <CardHeader className="flex flex-row items-center justify-between space-y-0 pb-2">
                <CardTitle className="text-sm font-medium">High Priority</CardTitle>
                <TrendingUp className="h-4 w-4 text-orange-600" />
              </CardHeader>
              <CardContent>
                <div className="text-2xl font-bold text-orange-600">{summary.high}</div>
                <p className="text-xs text-muted-foreground">
                  Review within 24 hours
                </p>
              </CardContent>
            </Card>

            <Card>
              <CardHeader className="flex flex-row items-center justify-between space-y-0 pb-2">
                <CardTitle className="text-sm font-medium">Actioned</CardTitle>
                <CheckCircle className="h-4 w-4 text-green-600" />
              </CardHeader>
              <CardContent>
                <div className="text-2xl font-bold text-green-600">
                  {insights.filter(i => i.isActioned).length}
                </div>
                <p className="text-xs text-muted-foreground">
                  Completed actions
                </p>
              </CardContent>
            </Card>
          </div>

          {/* Insight Types Distribution */}
          <Card>
            <CardHeader>
              <CardTitle>Insights by Type</CardTitle>
            </CardHeader>
            <CardContent>
              <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-4 gap-4">
                {INSIGHT_TYPES.slice(1).map((type) => {
                  const Icon = type.icon;
                  const count = summary.byType[type.value] || 0;
                  return (
                    <div key={type.value} className="flex items-center space-x-3 p-3 bg-gray-50 rounded-lg">
                      <Icon className="w-8 h-8 text-blue-600" />
                      <div>
                        <p className="text-sm text-gray-600">{type.label}</p>
                        <p className="text-lg font-semibold">{count}</p>
                      </div>
                    </div>
                  );
                })}
              </div>
            </CardContent>
          </Card>

          {/* Recent Critical Insights */}
          {summary.critical > 0 && (
            <Card>
              <CardHeader>
                <CardTitle className="text-red-600 flex items-center space-x-2">
                  <AlertTriangle className="w-5 h-5" />
                  <span>Critical Insights</span>
                </CardTitle>
              </CardHeader>
              <CardContent>
                <div className="space-y-4">
                  {insights
                    .filter(i => i.severity === "critical")
                    .slice(0, 3)
                    .map(renderInsightCard)}
                </div>
              </CardContent>
            </Card>
          )}
        </TabsContent>

        <TabsContent value="insights" className="space-y-6">
          {/* Filters */}
          <Card>
            <CardHeader>
              <CardTitle>Filter Insights</CardTitle>
            </CardHeader>
            <CardContent>
              <div className="flex items-center space-x-4">
                <div className="flex items-center space-x-2">
                  <span className="text-sm font-medium">Type:</span>
                  <select 
                    value={selectedType} 
                    onChange={(e) => setSelectedType(e.target.value)}
                    className="border rounded px-2 py-1 text-sm"
                  >
                    {INSIGHT_TYPES.map((type) => (
                      <option key={type.value} value={type.value}>
                        {type.label}
                      </option>
                    ))}
                  </select>
                </div>
                <div className="flex items-center space-x-2">
                  <span className="text-sm font-medium">Severity:</span>
                  <select 
                    value={selectedSeverity} 
                    onChange={(e) => setSelectedSeverity(e.target.value)}
                    className="border rounded px-2 py-1 text-sm"
                  >
                    {SEVERITY_LEVELS.map((level) => (
                      <option key={level.value} value={level.value}>
                        {level.label}
                      </option>
                    ))}
                  </select>
                </div>
              </div>
            </CardContent>
          </Card>

          {/* Insights List */}
          <Card>
            <CardHeader>
              <CardTitle>
                {filteredInsights.length} Insights
                {selectedType !== "all" && ` (${selectedType})`}
                {selectedSeverity !== "all" && ` (${selectedSeverity})`}
              </CardTitle>
            </CardHeader>
            <CardContent>
              {isLoading ? (
                <div className="flex items-center justify-center h-32">
                  <div className="animate-spin rounded-full h-8 w-8 border-b-2 border-primary"></div>
                </div>
              ) : filteredInsights.length === 0 ? (
                <div className="text-center py-8 text-gray-500">
                  <Brain className="w-12 h-12 mx-auto mb-4 text-gray-300" />
                  <p>No insights match your filters</p>
                </div>
              ) : (
                <div className="space-y-4">
                  {filteredInsights.map(renderInsightCard)}
                </div>
              )}
            </CardContent>
          </Card>
        </TabsContent>

        <TabsContent value="actions" className="space-y-6">
          {/* Action Items Summary */}
          <Card>
            <CardHeader>
              <CardTitle>Action Items Summary</CardTitle>
            </CardHeader>
            <CardContent>
              <div className="grid grid-cols-1 md:grid-cols-3 gap-4">
                <div className="text-center p-4 bg-red-50 rounded-lg">
                  <div className="text-2xl font-bold text-red-600 mb-2">
                    {insights.filter(i => i.severity === "critical" && !i.isActioned).length}
                  </div>
                  <p className="text-sm text-gray-600">Critical Actions</p>
                </div>
                <div className="text-center p-4 bg-orange-50 rounded-lg">
                  <div className="text-2xl font-bold text-orange-600 mb-2">
                    {insights.filter(i => i.severity === "high" && !i.isActioned).length}
                  </div>
                  <p className="text-sm text-gray-600">High Priority</p>
                </div>
                <div className="text-center p-4 bg-green-50 rounded-lg">
                  <div className="text-2xl font-bold text-green-600 mb-2">
                    {insights.filter(i => i.isActioned).length}
                  </div>
                  <p className="text-sm text-gray-600">Completed</p>
                </div>
              </div>
            </CardContent>
          </Card>

          {/* Pending Actions */}
          <Card>
            <CardHeader>
              <CardTitle>Pending Actions</CardTitle>
            </CardHeader>
            <CardContent>
              <div className="space-y-4">
                {insights
                  .filter(i => !i.isActioned && (i.severity === "critical" || i.severity === "high"))
                  .map(renderInsightCard)}
              </div>
            </CardContent>
          </Card>
        </TabsContent>
      </Tabs>
    </div>
  );
}

import { TrendingUp, TrendingDown, DollarSign, ShoppingCart, Package, Users } from "lucide-react";
import { Card, CardContent, CardHeader, CardTitle } from "@/components/ui/card";
import { formatCurrency } from "@/lib/pos-utils";

interface DashboardCardsProps {
  dailySales: {
    revenue: number;
    transactions: number;
  };
  profitLoss: {
    revenue: number;
    cost: number;
    profit: number;
  };
  popularProducts: Array<{
    product: { id: string; name: string; price: string };
    salesCount: number;
  }>;
  additionalMetrics?: {
    totalProducts?: number;
    totalCustomers?: number;
    averageOrderValue?: number;
  };
}

export default function DashboardCards({
  dailySales,
  profitLoss,
  popularProducts,
  additionalMetrics = {},
}: DashboardCardsProps) {
  const profitMargin = profitLoss.revenue > 0 ? (profitLoss.profit / profitLoss.revenue) * 100 : 0;
  const avgOrderValue = dailySales.transactions > 0 ? dailySales.revenue / dailySales.transactions : 0;
  const topProductSales = popularProducts.length > 0 ? popularProducts[0].salesCount : 0;

  return (
    <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-4 gap-6">
      {/* Today's Revenue */}
      <Card>
        <CardHeader className="flex flex-row items-center justify-between space-y-0 pb-2">
          <CardTitle className="text-sm font-medium">Today's Revenue</CardTitle>
          <DollarSign className="h-4 w-4 text-muted-foreground" />
        </CardHeader>
        <CardContent>
          <div className="text-2xl font-bold text-green-600">
            {formatCurrency(dailySales.revenue)}
          </div>
          <p className="text-xs text-muted-foreground">
            {dailySales.transactions} transactions
          </p>
        </CardContent>
      </Card>

      {/* Monthly Profit */}
      <Card>
        <CardHeader className="flex flex-row items-center justify-between space-y-0 pb-2">
          <CardTitle className="text-sm font-medium">Monthly Profit</CardTitle>
          {profitLoss.profit >= 0 ? (
            <TrendingUp className="h-4 w-4 text-green-600" />
          ) : (
            <TrendingDown className="h-4 w-4 text-red-600" />
          )}
        </CardHeader>
        <CardContent>
          <div className={`text-2xl font-bold ${profitLoss.profit >= 0 ? 'text-green-600' : 'text-red-600'}`}>
            {formatCurrency(profitLoss.profit)}
          </div>
          <p className="text-xs text-muted-foreground">
            {profitMargin.toFixed(1)}% margin
          </p>
        </CardContent>
      </Card>

      {/* Average Order Value */}
      <Card>
        <CardHeader className="flex flex-row items-center justify-between space-y-0 pb-2">
          <CardTitle className="text-sm font-medium">Avg Order Value</CardTitle>
          <ShoppingCart className="h-4 w-4 text-muted-foreground" />
        </CardHeader>
        <CardContent>
          <div className="text-2xl font-bold">
            {formatCurrency(avgOrderValue)}
          </div>
          <p className="text-xs text-muted-foreground">
            Per transaction
          </p>
        </CardContent>
      </Card>

      {/* Top Product Sales */}
      <Card>
        <CardHeader className="flex flex-row items-center justify-between space-y-0 pb-2">
          <CardTitle className="text-sm font-medium">Top Product Sales</CardTitle>
          <Package className="h-4 w-4 text-muted-foreground" />
        </CardHeader>
        <CardContent>
          <div className="text-2xl font-bold text-blue-600">
            {topProductSales}
          </div>
          <p className="text-xs text-muted-foreground">
            {popularProducts.length > 0 ? popularProducts[0].product.name : 'No data'}
          </p>
        </CardContent>
      </Card>

      {/* Monthly Revenue */}
      <Card>
        <CardHeader className="flex flex-row items-center justify-between space-y-0 pb-2">
          <CardTitle className="text-sm font-medium">Monthly Revenue</CardTitle>
          <TrendingUp className="h-4 w-4 text-muted-foreground" />
        </CardHeader>
        <CardContent>
          <div className="text-2xl font-bold">
            {formatCurrency(profitLoss.revenue)}
          </div>
          <p className="text-xs text-muted-foreground">
            Last 30 days
          </p>
        </CardContent>
      </Card>

      {/* Monthly Costs */}
      <Card>
        <CardHeader className="flex flex-row items-center justify-between space-y-0 pb-2">
          <CardTitle className="text-sm font-medium">Monthly Costs</CardTitle>
          <TrendingDown className="h-4 w-4 text-red-600" />
        </CardHeader>
        <CardContent>
          <div className="text-2xl font-bold text-red-600">
            {formatCurrency(profitLoss.cost)}
          </div>
          <p className="text-xs text-muted-foreground">
            Cost of goods sold
          </p>
        </CardContent>
      </Card>

      {/* Total Products */}
      {additionalMetrics.totalProducts !== undefined && (
        <Card>
          <CardHeader className="flex flex-row items-center justify-between space-y-0 pb-2">
            <CardTitle className="text-sm font-medium">Total Products</CardTitle>
            <Package className="h-4 w-4 text-muted-foreground" />
          </CardHeader>
          <CardContent>
            <div className="text-2xl font-bold">
              {additionalMetrics.totalProducts}
            </div>
            <p className="text-xs text-muted-foreground">
              In catalog
            </p>
          </CardContent>
        </Card>
      )}

      {/* Total Customers */}
      {additionalMetrics.totalCustomers !== undefined && (
        <Card>
          <CardHeader className="flex flex-row items-center justify-between space-y-0 pb-2">
            <CardTitle className="text-sm font-medium">Total Customers</CardTitle>
            <Users className="h-4 w-4 text-muted-foreground" />
          </CardHeader>
          <CardContent>
            <div className="text-2xl font-bold">
              {additionalMetrics.totalCustomers}
            </div>
            <p className="text-xs text-muted-foreground">
              Registered
            </p>
          </CardContent>
        </Card>
      )}
    </div>
  );
}

import { useQuery } from "@tanstack/react-query";
import { TrendingUp, TrendingDown, Brain, Zap, AlertTriangle, CheckCircle, Clock, Target, BarChart3, LineChart as LineChartIcon } from "lucide-react";
import { useState, useEffect } from "react";
import { LineChart, Line, BarChart, Bar, XAxis, YAxis, CartesianGrid, Tooltip, Legend, ResponsiveContainer, AreaChart, Area } from "recharts";
import { Alert, AlertDescription } from "@/components/ui/alert";
import { Badge } from "@/components/ui/badge";
import { Button } from "@/components/ui/button";
import { Card, CardContent, CardHeader, CardTitle } from "@/components/ui/card";
import { Select, SelectContent, SelectItem, SelectTrigger, SelectValue } from "@/components/ui/select";
import { apiClient } from "@/lib/api-client";
import { formatCurrency, formatDate } from "@/lib/pos-utils";
import { apiRequest } from "@/lib/queryClient";
import { cn } from "@/lib/utils";

interface ForecastData {
  date: string;
  actualDemand: number;
  predictedDemand: number;
  confidenceLower: number;
  confidenceUpper: number;
  accuracy: number;
}

interface ForecastModel {
  id: string;
  name: string;
  modelType: string;
  accuracy: number;
  lastTrained: string;
  isActive: boolean;
}

interface AiInsight {
  id: string;
  insightType: string;
  title: string;
  description: string;
  severity: string;
  isRead: boolean;
  createdAt: string;
}

interface DemandForecastProps {
  storeId: string;
  className?: string;
}

const FORECAST_PERIODS = [
  { value: "7", label: "Next 7 Days" },
  { value: "14", label: "Next 14 Days" },
  { value: "30", label: "Next 30 Days" },
  { value: "90", label: "Next 90 Days" },
];

const MODEL_TYPES = [
  { value: "linear", label: "Linear Regression", description: "Simple trend-based forecasting" },
  { value: "arima", label: "ARIMA", description: "Advanced time series analysis" },
  { value: "lstm", label: "LSTM Neural Network", description: "Deep learning for complex patterns" },
  { value: "prophet", label: "Prophet", description: "Facebook's forecasting tool" },
  { value: "ensemble", label: "Ensemble", description: "Combined multiple models" },
];

export default function DemandForecast({ storeId, className }: DemandForecastProps) {
  const [forecastPeriod, setForecastPeriod] = useState("30");
  const [selectedModel, setSelectedModel] = useState<string>("");
  const [chartType, setChartType] = useState("line");

  // Fetch forecast models from AI status (mocked list via status)
  const { data: models = [] } = useQuery<ForecastModel[]>({
    queryKey: ["/api/ai/status", storeId],
    queryFn: async () => {
      const status: any = await apiClient.get<any>(`/ai/status`);
      const m = status?.models?.forecasting
        ? [{
            id: 'forecasting-default',
            name: 'Ensemble Forecasting',
            modelType: status.models.forecasting.type || 'ensemble',
            accuracy: status.models.forecasting.accuracy || 0.85,
            lastTrained: status.models.forecasting.lastTrained || new Date().toISOString(),
            isActive: true,
          }]
        : [];
      return m as ForecastModel[];
    },
  });

  // Fetch demand forecasts
  const { data: forecastData = [], isLoading } = useQuery<ForecastData[]>({
    queryKey: ["/api/ai/forecast", storeId, forecastPeriod, selectedModel],
    queryFn: async () => {
      const resp: any = await apiClient.get<any>(`/ai/forecast`, {
        storeId,
        days: forecastPeriod,
      });
      // Server returns { enabled, forecasts, metadata }
      const f = Array.isArray(resp?.forecasts) ? resp.forecasts : [];
      // Map to chart-friendly structure if needed
      return f.map((it: any, idx: number) => ({
        date: it.forecastDate || new Date(Date.now() + (idx + 1) * 86400000).toISOString(),
        actualDemand: it.historicalData?.slice(-1)?.[0]?.quantity ?? 0,
        predictedDemand: Math.round(it.predictedDemand || 0),
        confidenceLower: Math.max(0, Math.round((it.predictedDemand || 0) * 0.8)),
        confidenceUpper: Math.round((it.predictedDemand || 0) * 1.2),
        accuracy: Math.round(((it.confidence || 0.8) * 100)),
      })) as ForecastData[];
    },
    enabled: !!storeId,
  });

  // Fetch AI insights
  const { data: insights = [] } = useQuery<AiInsight[]>({
    queryKey: ["/api/ai/insights", storeId],
    queryFn: async () => {
      const resp: any = await apiClient.get<any>(`/ai/insights`, { storeId });
      const raw = Array.isArray(resp?.insights) ? resp.insights : [];
      return raw.map((ins: any, idx: number) => ({
        id: ins.id || `${idx}-${ins.generatedAt || Date.now()}`,
        insightType: ins.category || 'general',
        title: ins.title || 'Insight',
        description: ins.description || '',
        severity: ins.priority || 'medium',
        isRead: false,
        createdAt: ins.generatedAt || new Date().toISOString(),
      })) as AiInsight[];
    },
  });

  // Auto-select first model when models are loaded
  useEffect(() => {
    if (models.length > 0 && !selectedModel) {
      setSelectedModel(models[0].id);
    }
  }, [models, selectedModel]);

  const currentModel = models.find(m => m.id === selectedModel);
  const unreadInsights = insights.filter(i => !i.isRead);
  const criticalInsights = insights.filter(i => i.severity === "critical");

  const renderForecastChart = () => {
    if (isLoading) {
      return (
        <div className="flex items-center justify-center h-64">
          <div className="animate-spin rounded-full h-8 w-8 border-b-2 border-primary"></div>
        </div>
      );
    }

    if (forecastData.length === 0) {
      return (
        <div className="flex items-center justify-center h-64 text-gray-500">
          <div className="text-center">
            <Brain className="w-12 h-12 mx-auto mb-4 text-gray-300" />
            <p>No forecast data available</p>
            <p className="text-sm">Select a model and period to generate forecasts</p>
          </div>
        </div>
      );
    }

    switch (chartType) {
      case "line":
        return (
          <ResponsiveContainer width="100%" height={400}>
            <LineChart data={forecastData}>
              <CartesianGrid strokeDasharray="3 3" />
              <XAxis 
                dataKey="date" 
                tickFormatter={(value) => formatDate(new Date(value), "MMM dd")}
              />
              <YAxis domain={[0, 'dataMax + 10']} />
              <Tooltip 
                formatter={(value: any, name: string) => [
                  value,
                  name === 'actualDemand' ? 'Actual Demand' : 
                  name === 'predictedDemand' ? 'Predicted Demand' : 
                  name === 'confidenceLower' ? 'Lower Bound' : 'Upper Bound'
                ]}
                labelFormatter={(label) => formatDate(new Date(label), "MMM dd, yyyy")}
              />
              <Legend />
              <Line 
                type="monotone" 
                dataKey="actualDemand" 
                stroke="#3B82F6" 
                strokeWidth={2}
                dot={{ fill: "#3B82F6", strokeWidth: 2, r: 4 }}
                activeDot={{ r: 6 }}
                name="Actual Demand"
              />
              <Line 
                type="monotone" 
                dataKey="predictedDemand" 
                stroke="#10B981" 
                strokeWidth={2}
                strokeDasharray="5 5"
                dot={{ fill: "#10B981", strokeWidth: 2, r: 4 }}
                activeDot={{ r: 6 }}
                name="Predicted Demand"
              />
              <Line 
                type="monotone" 
                dataKey="confidenceUpper" 
                stroke="#F59E0B" 
                strokeWidth={1}
                strokeDasharray="3 3"
                dot={false}
                name="Confidence Upper"
              />
              <Line 
                type="monotone" 
                dataKey="confidenceLower" 
                stroke="#F59E0B" 
                strokeWidth={1}
                strokeDasharray="3 3"
                dot={false}
                name="Confidence Lower"
              />
            </LineChart>
          </ResponsiveContainer>
        );

      case "area":
        return (
          <ResponsiveContainer width="100%" height={400}>
            <AreaChart data={forecastData}>
              <CartesianGrid strokeDasharray="3 3" />
              <XAxis 
                dataKey="date" 
                tickFormatter={(value) => formatDate(new Date(value), "MMM dd")}
              />
              <YAxis domain={[0, 'dataMax + 10']} />
              <Tooltip 
                formatter={(value: any, name: string) => [
                  value,
                  name === 'actualDemand' ? 'Actual Demand' : 'Predicted Demand'
                ]}
                labelFormatter={(label) => formatDate(new Date(label), "MMM dd, yyyy")}
              />
              <Legend />
              <Area 
                type="monotone" 
                dataKey="actualDemand" 
                stackId="1"
                stroke="#3B82F6" 
                fill="#3B82F6" 
                fillOpacity={0.6}
                name="Actual Demand"
              />
              <Area 
                type="monotone" 
                dataKey="predictedDemand" 
                stackId="2"
                stroke="#10B981" 
                fill="#10B981" 
                fillOpacity={0.6}
                name="Predicted Demand"
              />
            </AreaChart>
          </ResponsiveContainer>
        );

      case "bar":
        return (
          <ResponsiveContainer width="100%" height={400}>
            <BarChart data={forecastData}>
              <CartesianGrid strokeDasharray="3 3" />
              <XAxis 
                dataKey="date" 
                tickFormatter={(value) => formatDate(new Date(value), "MMM dd")}
              />
              <YAxis domain={[0, 'dataMax + 10']} />
              <Tooltip 
                formatter={(value: any, name: string) => [
                  value,
                  name === 'actualDemand' ? 'Actual Demand' : 'Predicted Demand'
                ]}
                labelFormatter={(label) => formatDate(new Date(label), "MMM dd, yyyy")}
              />
              <Legend />
              <Bar dataKey="actualDemand" fill="#3B82F6" radius={[4, 4, 0, 0]} name="Actual Demand" />
              <Bar dataKey="predictedDemand" fill="#10B981" radius={[4, 4, 0, 0]} name="Predicted Demand" />
            </BarChart>
          </ResponsiveContainer>
        );

      default:
        return null;
    }
  };

  const getSeverityColor = (severity: string) => {
    switch (severity) {
      case "critical": return "text-red-600 bg-red-50 border-red-200";
      case "high": return "text-orange-600 bg-orange-50 border-orange-200";
      case "medium": return "text-yellow-600 bg-yellow-50 border-yellow-200";
      case "low": return "text-green-600 bg-green-50 border-green-200";
      default: return "text-gray-600 bg-gray-50 border-gray-200";
    }
  };

  const getSeverityIcon = (severity: string) => {
    switch (severity) {
      case "critical": return AlertTriangle;
      case "high": return AlertTriangle;
      case "medium": return Clock;
      case "low": return CheckCircle;
      default: return Clock;
    }
  };

  return (
    <div className={cn("space-y-6", className)}>
      {/* AI Insights Alerts */}
      {criticalInsights.length > 0 && (
        <Alert className="border-red-200 bg-red-50">
          <AlertTriangle className="h-4 w-4 text-red-600" />
          <AlertDescription className="text-red-800">
            <strong>{criticalInsights.length} critical AI insights</strong> require immediate attention. 
            <Button variant="link" className="p-0 h-auto text-red-800 underline">
              View insights
            </Button>
          </AlertDescription>
        </Alert>
      )}

      {/* Forecast Controls */}
      <Card>
        <CardHeader>
          <div className="flex items-center justify-between">
            <CardTitle className="text-xl flex items-center space-x-2">
              <Brain className="w-6 h-6 text-purple-600" />
              <span>AI Demand Forecasting</span>
            </CardTitle>
            <div className="flex items-center space-x-2">
              <Select value={chartType} onValueChange={setChartType}>
                <SelectTrigger className="w-32">
                  <SelectValue />
                </SelectTrigger>
                <SelectContent>
                  <SelectItem value="line">
                    <div className="flex items-center space-x-2">
                      <LineChartIcon className="w-4 h-4" />
                      <span>Line</span>
                    </div>
                  </SelectItem>
                  <SelectItem value="area">
                    <div className="flex items-center space-x-2">
                      <BarChart3 className="w-4 h-4" />
                      <span>Area</span>
                    </div>
                  </SelectItem>
                  <SelectItem value="bar">
                    <div className="flex items-center space-x-2">
                      <BarChart3 className="w-4 h-4" />
                      <span>Bar</span>
                    </div>
                  </SelectItem>
                </SelectContent>
              </Select>

              <Select value={forecastPeriod} onValueChange={setForecastPeriod}>
                <SelectTrigger className="w-40">
                  <SelectValue />
                </SelectTrigger>
                <SelectContent>
                  {FORECAST_PERIODS.map((period) => (
                    <SelectItem key={period.value} value={period.value}>
                      {period.label}
                    </SelectItem>
                  ))}
                </SelectContent>
              </Select>

              <Select value={selectedModel} onValueChange={setSelectedModel}>
                <SelectTrigger className="w-48">
                  <SelectValue placeholder="Select Model" />
                </SelectTrigger>
                <SelectContent>
                  {models.map((model) => (
                    <SelectItem key={model.id} value={model.id}>
                      <div className="flex items-center space-x-2">
                        <span>{model.name}</span>
                        <Badge variant="outline" className="text-xs">
                          {model.modelType}
                        </Badge>
                      </div>
                    </SelectItem>
                  ))}
                </SelectContent>
              </Select>
            </div>
          </div>
        </CardHeader>

        <CardContent>
          {/* Model Performance Summary */}
          {currentModel && (
            <div className="grid grid-cols-1 md:grid-cols-4 gap-4 mb-6">
              <div className="flex items-center space-x-3 p-3 bg-purple-50 rounded-lg">
                <Brain className="w-8 h-8 text-purple-600" />
                <div>
                  <p className="text-sm text-gray-600">Model Accuracy</p>
                  <p className="text-lg font-semibold text-purple-600">
                    {((currentModel.accuracy || 0) * 100).toFixed(1)}%
                  </p>
                </div>
              </div>

              <div className="flex items-center space-x-3 p-3 bg-blue-50 rounded-lg">
                <Target className="w-8 h-8 text-blue-600" />
                <div>
                  <p className="text-sm text-gray-600">Model Type</p>
                  <p className="text-lg font-semibold text-blue-600">
                    {currentModel.modelType.toUpperCase()}
                  </p>
                </div>
              </div>

              <div className="flex items-center space-x-3 p-3 bg-green-50 rounded-lg">
                <Clock className="w-8 h-8 text-green-600" />
                <div>
                  <p className="text-sm text-gray-600">Last Trained</p>
                  <p className="text-lg font-semibold text-green-600">
                    {currentModel.lastTrained ? formatDate(new Date(currentModel.lastTrained), "MMM dd") : "Never"}
                  </p>
                </div>
              </div>

              <div className="flex items-center space-x-3 p-3 bg-orange-50 rounded-lg">
                <Zap className="w-8 h-8 text-orange-600" />
                <div>
                  <p className="text-sm text-gray-600">Status</p>
                  <p className="text-lg font-semibold text-orange-600">
                    {currentModel.isActive ? "Active" : "Inactive"}
                  </p>
                </div>
              </div>
            </div>
          )}

          {/* Forecast Chart */}
          {renderForecastChart()}
        </CardContent>
      </Card>

      {/* AI Insights */}
      <Card>
        <CardHeader>
          <CardTitle className="text-xl flex items-center space-x-2">
            <Zap className="w-6 h-6 text-yellow-600" />
            <span>AI Insights</span>
            {unreadInsights.length > 0 && (
              <Badge variant="destructive" className="ml-2">
                {unreadInsights.length} new
              </Badge>
            )}
          </CardTitle>
        </CardHeader>
        <CardContent>
          {insights.length === 0 ? (
            <div className="text-center py-8 text-gray-500">
              <Brain className="w-12 h-12 mx-auto mb-4 text-gray-300" />
              <p>No AI insights available</p>
              <p className="text-sm">AI will generate insights as it analyzes your data</p>
            </div>
          ) : (
            <div className="space-y-4">
              {insights.slice(0, 5).map((insight) => {
                const Icon = getSeverityIcon(insight.severity);
                return (
                  <div
                    key={insight.id}
                    className={cn(
                      "p-4 rounded-lg border",
                      getSeverityColor(insight.severity),
                      !insight.isRead && "ring-2 ring-blue-200"
                    )}
                  >
                    <div className="flex items-start space-x-3">
                      <Icon className="w-5 h-5 mt-0.5 flex-shrink-0" />
                      <div className="flex-1">
                        <div className="flex items-center space-x-2 mb-1">
                          <h4 className="font-medium">{insight.title}</h4>
                          <Badge variant="outline" className="text-xs">
                            {insight.insightType}
                          </Badge>
                          {!insight.isRead && (
                            <Badge variant="default" className="text-xs">
                              New
                            </Badge>
                          )}
                        </div>
                        <p className="text-sm mb-2">{insight.description}</p>
                        <div className="flex items-center justify-between">
                          <span className="text-xs text-gray-500">
                            {formatDate(new Date(insight.createdAt), "MMM dd, yyyy")}
                          </span>
                          <Button variant="ghost" size="sm">
                            View Details
                          </Button>
                        </div>
                      </div>
                    </div>
                  </div>
                );
              })}
            </div>
          )}
        </CardContent>
      </Card>

      {/* Model Management */}
      <Card>
        <CardHeader>
          <CardTitle className="text-xl">Forecast Models</CardTitle>
        </CardHeader>
        <CardContent>
          <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-4">
            {MODEL_TYPES.map((modelType) => (
              <div key={modelType.value} className="p-4 border rounded-lg hover:border-primary/50 transition-colors">
                <h4 className="font-medium mb-2">{modelType.label}</h4>
                <p className="text-sm text-gray-600 mb-3">{modelType.description}</p>
                <Button variant="outline" size="sm" className="w-full">
                  Train Model
                </Button>
              </div>
            ))}
          </div>
        </CardContent>
      </Card>
    </div>
  );
}

